document.addEventListener("DOMContentLoaded",(function(){const e=document.querySelector(".techfusion-preview");function t(e){return`\n            <div class="preview-card">\n                <div class="preview-image-container">\n                    <img src="${e.image}" alt="${e.title}" class="preview-image">\n                </div>\n                <div class="preview-content">\n                    <h3 class="preview-title">${e.title}</h3>\n                    <div class="preview-date">${e.date}</div>\n                    <p class="preview-description">${e.description}</p>\n                </div>\n            </div>\n        `}!async function(){const n=await async function(){try{const e=await fetch("/pages/events.html"),t=await e.text(),n=(new DOMParser).parseFromString(t,"text/html");return Array.from(n.querySelectorAll(".carousel .card")).slice(0,3).map((e=>({title:e.querySelector(".card-title").textContent,date:e.querySelector(".card-date").textContent,description:e.querySelector(".event-details").textContent,image:e.querySelector(".event-image")?.getAttribute("src")||"/images/events/default.jpg"})))}catch(e){return console.error("Error fetching preview events:",e),[]}}();0!==n.length?e.innerHTML=`\n            <div class="preview-header">\n                <h2>TechFusion Preview</h2>\n                <a href="/pages/events.html" class="view-all-btn">View All Events</a>\n            </div>\n            <div class="preview-grid">\n                ${n.map(t).join("")}\n            </div>\n        `:e.innerHTML='<p class="no-preview">Stay tuned for upcoming events!</p>'}()}));